import React, { useEffect, useState, Component } from 'react';
import { re, add, add1, cancel } from "../js/back"
import "./Notice.css";
import Axios from "axios";

export default function Noticeboard() {
    const [TitleValue, setTitleValue] = useState("")
    const [ContentValue, setContentValue] = useState("")
    const submitTest = () => {

    document.querySelector(".lll").classList.remove("display-off");
    document.getElementById("addlist").classList.add("display-off")
    document.getElementById("addlist").classList.remove("poi")
    Axios.get("http://localhost:4000/", {}).then(() => {
        alert("등록 완료!");

    });
};
let boarddata;
Axios.get("http://localhost:4000/list", {})
    .then((res) => {
        const { data } = res;
        boarddata = data
        console.log(boarddata)
        for(var i=0; i <boarddata.length;i++){
            console.log(boarddata[i].title)
            console.log(boarddata[i].name)
            var number = 0;
    var temp3;
    var rediv;
    let today = new Date();
    let year = today.getFullYear(); // 년도
    let month = today.getMonth() + 1;  // 월
    let date = today.getDate();  // 날짜

    number = number + 1;
    const newDiv1 = document.createElement('a');
    newDiv1.setAttribute('id', "div" + number);
    newDiv1.setAttribute('class', "mnb");
    newDiv1.setAttribute('href', "#");

    const newDiv3 = document.createElement('div');
    newDiv3.setAttribute('class', "num-1");
    const newText3 = document.createTextNode(number);
    newDiv3.appendChild(newText3);
    newDiv1.appendChild(newDiv3);

    const newDiv4 = document.createElement('div');
    newDiv4.setAttribute('class', "noticetitle-1");
    const newText4 = document.createTextNode(temp3);
    newDiv4.appendChild(newText4);
    newDiv1.appendChild(newDiv4);

    const newDiv5 = document.createElement('div');
    newDiv5.setAttribute('class', "writer-1");
    const newText5 = document.createTextNode("관리자");
    newDiv5.appendChild(newText5);
    newDiv1.appendChild(newDiv5);

    const newDiv6 = document.createElement('div');
    newDiv6.setAttribute('class', "time-1");
    const newText6 = document.createTextNode(year + '/' + month + '/' + date);
    newDiv6.appendChild(newText6);
    newDiv1.appendChild(newDiv6);

    const newDiv7 = document.createElement('div');
    newDiv7.setAttribute('class', "view-1");
    const newText7 = document.createTextNode(number);
    newDiv7.appendChild(newText7);
    newDiv1.appendChild(newDiv7);
        // list.appendChild(newDiv1);
    document.getElementById("total_number").innerText = number;
    document.getElementById("tittle").value = null;
        }
    })


    const onTitleChange = (event) => {
        setTitleValue(event.currentTarget.value);
    };
    console.log(TitleValue);

    const onContentChange = (event) => {
        setContentValue(event.currentTarget.value);
    };
    console.log(ContentValue);

    return (
        <>
            <div className="lll">
                <div className="body-2">
                    <div className="main-box">
                        <div className="title-1-box" />
                        <p className="communication-info">공지사항</p>
                    </div>
                    <div className="total-search">
                        <div className="total-box">
                            <p className="total">총</p>
                            <p id="total_number" className="total-number">0</p>
                            <p className="total-gun">건</p>
                        </div>
                        <div className="search-a">
                            <input type="text" id="text" placeholder="검색어를 입력해주세요" />
                            <button onClick={add1}>추가</button>
                            <button>검색</button>
                        </div>
                    </div>
                    <div className="not">
                        <div className="lkj">
                            <div className="num">번호</div>
                            <div className="noticetitle">제목</div>
                            <div className="writer">작성자</div>
                            <div className="time">작성일</div>
                            <div className="view">조회수</div>
                        </div>
                        <div id="list" className="lkj-under">

                        </div>
                        <form className="rebox">
                            {/* <input onchange="re1()" class="reinput"type="text" id="reid" placeholder="삭제할 번호를 입력해주세요."> */}
                            <button onClick={re} type="reset">삭제</button>
                        </form>
                        <div className="next">
                            <div className="prev2">
                                &lt;&lt;</div>
                            <div className="prev">
                                &lt;</div>
                            <div className="num1">1</div>
                            <div className="num2">2</div>
                            <div className="num3">3</div>
                            <div className="num4">4</div>
                            <div className="num5">5</div>
                            <div className="next1">&gt;</div>
                            <div className="next2">&gt;&gt;</div>
                        </div>
                    </div>
                </div>
            </div>
            <form id="addlist" className="display-off">
                <div className="announce">공지사항 </div>
                <div className="tittle">제목
                    <input onChange={onTitleChange} name="title" value={TitleValue} type="text" id="tittle" style={{ marginLeft: '20px', marginTop: '10px' }} />
                </div>
                <div className="aaa">내용
                    <input onChange={onContentChange} value={ContentValue} name='content' type="text" id="aaa" style={{ marginLeft: '20px', marginTop: '10px' }} />
                </div>
                <div className="button_box">
                    <button onClick={submitTest} id="save" className="save">저장</button>
                    <button onClick={cancel} className="cancel">취소</button>
                </div>
            </form>
        </>
    )
}
