/* global kakao */
import React, { useEffect } from "react";
import cn from "classnames";
import "../html/KakaoMap.css";

const { kakao } = window;

const Map = () => {
  var geocoder = new kakao.maps.services.Geocoder();

  useEffect(() => {
    let container = document.getElementById("map");

    let options = {
      center: new window.kakao.maps.LatLng(35.94828046699644, 126.95783758044811),
      level: 3,

    };

    let map = new window.kakao.maps.Map(container, options);
    //드래그 막기
    console.log("loading kakaomap");
geocoder.addressSearch('경기 성남시 분당구 판교역로 235 에이치스퀘어', function(result, status) {

    // 정상적으로 검색이 완료됐으면 
     if (status === kakao.maps.services.Status.OK) {

        var coords = new kakao.maps.LatLng(result[0].y, result[0].x);
		var message = 'latlng: new kakao.maps.LatLng(' + result[0].y + ', ';
		message += result[0].x + ')';

		var resultDiv = document.getElementById('clickLatlng'); 
		resultDiv.innerHTML = message;

        // 결과값으로 받은 위치를 마커로 표시합니다
        var marker = new kakao.maps.Marker({
            map: map,
            position: coords
        });

        // 인포윈도우로 장소에 대한 설명을 표시합니다
        var infowindow = new kakao.maps.InfoWindow({
            content: '<div style="width:150px;text-align:center;padding:6px 0;">장소</div>'
        });
        infowindow.open(map, marker);

        // 지도의 중심을 결과값으로 받은 위치로 이동시킵니다
        map.setCenter(coords);
    } 
});  
  }, []);

  return (
    <div className={cn("Map")}>
      <div className={cn("MapContainer")} id="map">
      </div>
    </div>
  );
};


export default Map;
