import React, { useState, useEffect } from 'react';
import DaumPostcode from "react-daum-postcode";
import KakaoMap from "../html/KakaoMap"
import Sidebar2 from "../html/Communication/Sidebar2"

const PopupPostCode = () => {
  const [fullAddressname, setFullAddressname] = useState();
  const [data, setData] = useState();
  // 우편번호 검색 후 주소 클릭 시 실행될 함수, data callback 용
  const handlePostCode = (data) => {
    let fullAddress = data.address;
    let extraAddress = '';

    if (data.addressType === 'R') {
      if (data.bname !== '') {
        extraAddress += data.bname;
      }
      if (data.buildingName !== '') {
        extraAddress += (extraAddress !== '' ? `, ${data.buildingName}` : data.buildingName);
      }
      fullAddress += (extraAddress !== '' ? ` (${extraAddress})` : '');
    }
    console.log(data)
    console.log(fullAddress)
    console.log(data.zonecode)
    document.getElementById("output").innerText = data.query
    setFullAddressname(fullAddress);
    setFullAddressname(data)
  }
  const Address_number = () => {

  }
  const postCodeStyle = {
    display: "block",
    position: "absolute",
    width: "365px",
    height: "560px",
  };

  // const postCode_btn = {
  //   width: "100px",
  //   position: "absolute",
  //   height: "50px",
  // };
  return (
    <div>
      <KakaoMap setAddress={fullAddressname} />
      <DaumPostcode style={postCodeStyle} onComplete={handlePostCode} autoClose={false}  onSearch={(fullAddressname.data) = {Address_number}}/>
    </div>
  )
}

export default PopupPostCode;
